from typing import Optional, Dict


def create_response(message: str, code: int = 200, data: Optional[Dict] = None):
    """All responses from the API should be generated by this function to keep the
    responses consistant.
    """
    return {"message": message, "code": code, "data": data}


def assert_str(string: Optional[str]) -> str:
    if isinstance(string, str):
        return string
    raise Exception(f"{type(string)} is not a str")
